import java.awt.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import javax.swing.*;public class Calculadora extends JFrame{		JPanel p = new JPanel((new GridLayout(4,1)));		JButton soma = new JButton("Soma");	JButton subtrai = new JButton("Subtrai");	JButton multiplica = new JButton("Multiplica");	JButton divide = new JButton("Divide");		JTextField num1 = new JTextField(10);	JTextField num2 = new JTextField(10);		JLabel result = new JLabel(" = " + resultado);		public static int resultado;		Calculadora(){		super("Calculadora");		setLayout(new FlowLayout());		operacao sinal = new operacao();		p.add(soma);		soma.addActionListener(sinal);		p.add(subtrai);		subtrai.addActionListener(sinal);			p.add(multiplica);		multiplica.addActionListener(sinal);		p.add(divide);		divide.addActionListener(sinal);				add(num1);				add(p);				add(num2);						add(result);					pack();		setVisible(true);		setDefaultCloseOperation(WindowConstants.DISPOSE_ON_CLOSE);			}		private class operacao implements ActionListener{						public void actionPerformed(ActionEvent evento) {						String string, string2;						if(evento.getSource() == soma) {				string = num1.getText();				string2 = num2.getText();				int numero1 = Integer.parseInt(string);				int numero2 = Integer.parseInt(string2);				resultado = numero1 + numero2;				result.setText(" = " + resultado);			}			else if(evento.getSource() == subtrai) {				string = num1.getText();				string2 = num2.getText();				int numero1 = Integer.parseInt(string);				int numero2 = Integer.parseInt(string2);				resultado = numero1 - numero2;				result.setText(" = " + resultado);			}			else if(evento.getSource() == multiplica) {				string = num1.getText();				string2 = num2.getText();				int numero1 = Integer.parseInt(string);				int numero2 = Integer.parseInt(string2);				resultado = numero1 * numero2;				result.setText(" = " + resultado);			}			else if(evento.getSource() == divide) {				string = num1.getText();				string2 = num2.getText();				int numero1 = Integer.parseInt(string);				int numero2 = Integer.parseInt(string2);				resultado = numero1 / numero2;				result.setText(" = " + resultado);			}		}	}		public static void main(String[] args) {		new Calculadora();	}}